    //----------------------------------------------------------
    public int findStopCodon(String dna, int startIndex, String stopCodon)
    {   
        int currIndex = dna.indexOf(stopCodon, startIndex+3);
        // xxxatgxxtaaatgxxxtaa
        while (currIndex !=-1)
        {
            if ((currIndex - startIndex) % 3 == 0)
            {
                return currIndex;
            }
            else
            {
                currIndex = dna.indexOf(stopCodon, currIndex+1);
            }

        }
        return dna.length(); 
    }
    //----------------------------------------------------------
    public String findGene(String dna,int where)
    {
        //dna = dna.toUpperCase();
        String gene = "";
        int startIndex = dna.indexOf("atg", where);
        if(startIndex == -1)
        {
            return gene;
        }
        int stopCodonTAA = findStopCodon(dna,startIndex,"taa");
        int stopCodonTAG = findStopCodon(dna,startIndex,"tag");
        int stopCodonTGA = findStopCodon(dna,startIndex,"tga");
        //case 1: find min of index stopTAA / stopTAG / stopTGA (that is not -1)
        int minTAA_TAG = Math.min(stopCodonTAA,stopCodonTAG);
        int minStopCodon = Math.min(stopCodonTGA, minTAA_TAG);
        // minStopCodon is not -1
        if(minStopCodon != -1 && minStopCodon != dna.length())
        {
            gene = dna.substring(startIndex, minStopCodon+3);
        }
        return gene;
    }
    //----------------------------------------------------------
    public void printAllGenes(String dna)
    {
        int startIndex = 0;
        String gene = "";
        while(true)
        {
            gene = findGene(dna, startIndex);
            System.out.println(gene);
            if(gene.isEmpty())
            {
                break;
            }
            startIndex = dna.indexOf(gene, startIndex)+ gene.length();
        }
    }
    //----------------------------------------------------------
    public StorageResource getAllGenes(String dna)
    {
        dna = dna.toLowerCase();
        StorageResource list = new StorageResource();
        int startIndex = 0;
        String gene = "";
        while(true)
        {
            gene = findGene(dna,startIndex);
            if(gene=="")
            {
                break;
            }
            list.add(gene);
            startIndex = dna.indexOf(gene, startIndex) + gene.length();
        }
        //System.out.println(list.size()); //"xxx atgxxxtaa xx atgtga xxx atgtaa atgtga"
        return list;
    }
