    //----------------------------------------------------------
    public int countCTG(String dna)
    {
        dna = dna.toUpperCase();
        int counter = 0;
        int index = 0;
        int start = 0;
        while (true)
        {
            index = dna.indexOf("CTG", start);
            if(index == -1)
            {
                break;
            }
            counter = counter + 1;
            start = index + 1;
        }
        return counter;
    }
    //----------------------------------------------------------
    public double cgRatio(String dna)
    {
        dna = dna.toUpperCase();
        int startIndex = 0;
        int len = dna.length();
        int cnum = cCounter(dna);
        int gnum = gCounter(dna);
        int sum = cnum + gnum;
        double ratio = (float)sum / len;

        return ratio;  
    }
    //----------------------------------------------------------
    public int cCounter(String dna)
    {
        dna = dna.toUpperCase();
        int counter = 0;
        int start =0;
        while (true)
        {
            int index = dna.indexOf("C",start);
            if (index == -1)
            {
                break;
            }
            counter = counter +1;
            start = index+1;
        }
        return counter;
    }
    //----------------------------------------------------------
    public int gCounter(String dna)
    {
        dna = dna.toUpperCase();
        int counter = 0;
        int start =0;
        while (true)
        {
            int index = dna.indexOf("G",start);
            if (index == -1)
            {
                break;
            }
            counter = counter +1;
            start = index+1;
        }
        return counter;
    }
